sys_ui_page.client_script:
//NOTE jQuery MUST START WITH A LOWER-CASE 'j'
jQuery('#myAjaxButton').click(function() {
	//alert("hello from my Button click!");
	var nameOfScriptInclude = "MyPDFGenerationServerSideScript";
	var methodOfScriptInclude = "makeMyPDF";
	var ga = new GlideAjax(nameOfScriptInclude);
	ga.addParam('sysparm_name',methodOfScriptInclude);
	ga.getXML(myCallBack);
});

function myCallBack(response) {
	var answer = response.responseXML.documentElement.getAttribute('answer');
	jQuery('#ajax_response_message').append("<div>hello from my Callback !!</div>")
								    .append('<p>' + answer + 'has been successfully created.');
	var jsonAnswer = JSON.parse(answer);
	jQuery('#ajax_response_message').append("<div>------------------------------------</div>")
								.append("<div>FirstName: " + jsonAnswer["First name"] + "</div>")
								.append("<div>Gender   : " + jsonAnswer["Gender"] + "</div>")
								.append("<div>LastName : " + jsonAnswer["Last name"] + "</div>")
								.append("<div>Terms    : " + jsonAnswer["Read Terms and Conditions"] + "</div>");

}
