sys_ui_page.client_script:
//NOTE jQuery MUST START WITH A LOWER-CASE 'j'
jQuery('#myAjaxButton').click(function() {
	//alert("hello from my Button click!");
	var nameOfScriptInclude = "MyPDFGenerationServerSideScript";
	var methodOfScriptInclude = "makeMyPDF";
	var ga = new GlideAjax(nameOfScriptInclude);
	ga.addParam('sysparm_name',methodOfScriptInclude);
	ga.getXML(myCallBack);
});

function myCallBack(response) {
	var answer = response.responseXML.documentElement.getAttribute('answer');
	alert("hello from my Callback !!");
	jQuery('#ajax_response_message').append('<p>' + answer[0] + 'has been successfully created.');
}
